const links=document.querySelectorAll(".modal__item");links.forEach(e=>{e.addEventListener("click",function(e){e.target.nextSibling&&e.target.nextSibling.classList.toggle("active")})});const btnMenu=document.querySelector("#mobile"),btnClose=document.querySelector("#close"),modalCategories=document.querySelector("#modalCategories");btnMenu.addEventListener("click",function(){modalCategories.classList.add("open")}),btnClose.addEventListener("click",function(){modalCategories.classList.remove("open")});